<?php \n$con = mysql_connect("localhost","****","***");\nif (!$con) {\n    die('Could not connect: ' . mysql_error());\n}\n\nmysql_select_db("******", $con);\n\n// Limit the query to fetch only the latest 5 articles\n$result = mysql_query("SELECT * FROM dbArticle ORDER BY timestamp DESC LIMIT 5");\n\n$first = true;    \nwhile($row = mysql_fetch_array($result)){\n    if($first){\n        echo "<h2 class=\"heading1\">". htmlspecialchars($row['title']) ."</h2>";\n        echo "By: ".htmlspecialchars($row['username'])." Type: ".htmlspecialchars($row['type'])." Posted: ".htmlspecialchars($row['timestamp']);\n        $body = $row['body'];\n        echo "<br/><p>".htmlspecialchars(substr($body, 0, 260))."...<span class=\"tool\"><a class=\"blue\" href=\"index.php?pageContent=readArticle&id=".urlencode($row['id'])."\">Read More</a></span></p><hr/>";\n        \n        $first = false;\n    } else {\n        echo "<h3>".htmlspecialchars($row['title'])."</h3>";\n        echo "By: ".htmlspecialchars($row['username'])."<br/>Posted: ".htmlspecialchars($row['timestamp']);\n        echo "...<span class=\"tool\"><a class=\"blue\" href=\"index.php?pageContent=readArticle&id=".urlencode($row['id'])."\">Read More</a></span><hr/>";\n    }\n}\n\nmysql_close($con);\n?>