<?php\n// ref_red.php\n\nif (!isset($_GET['referer']) || !isset($_GET['end'])) {\n    die('Referer and end parameters are required.');\n}\n\n$referer = filter_var($_GET['referer'], FILTER_VALIDATE_URL);\n$end = filter_var($_GET['end'], FILTER_VALIDATE_URL);\n\nif (!$referer || !$end) {\n    die('Invalid URL provided for referer or end parameter.');\n}\n\n$options = [\n    'http' => [\n        'method'  => 'GET',\n        'header'  => "Referer: $referer\r\n"\n    ]\n];\n\n$context = stream_context_create($options);\n\n$result = file_get_contents($end, false, $context);\nheader('Content-Type: text/html');\necho $result;\n?>