function getDomainName($url) {\n    $host = parse_url($url, PHP_URL_HOST);\n    $parts = explode('.', $host);\n\n    // Handle cases where the domain has multiple subdomains or is an IP address\n    if (count($parts) > 2 && !filter_var($host, FILTER_VALIDATE_IP)) {\n        return implode('.', array_slice($parts, -2));\n    } else {\n        return $host;\n    }\n}\n\n$url = "http://www.w3schools.com/PHP/func_string_str_split.asp";\n$domainName = getDomainName($url);\necho $domainName; // Outputs: w3schools.com