<?php\nsession_start();\n\nfunction getActiveUsers() {\n    $conn = new mysqli(DB_SERVER, DB_USERNAME, DB_PASSWORD, DB_NAME);\n    if ($conn->connect_error) die("Connection failed: " . $conn->connect_error);\n\n    // Consider a user active if they have interacted in the last 5 minutes.\n    $activeLimit = date('Y-m-d H:i:s', strtotime('-5 minutes'));\n\n    $stmt = $conn->prepare("SELECT user_id FROM user_sessions WHERE last_activity > ?");\n    $stmt->bind_param("s", $activeLimit);\n    $stmt->execute();\n    $result = $stmt->get_result();\n\n    $activeUsers = [];\n    while ($row = $result->fetch_assoc()) {\n        $activeUsers[] = $row['user_id'];\n    }\n\n    $stmt->close();\n    $conn->close();\n    \n    return $activeUsers;\n}\n\n$activeUsers = getActiveUsers();\necho "Active users: " . implode(", ", $activeUsers);\n?>