$searchreplace_array = Array('blah' => 'bleh', 'blarh' => 'blerh');\n$string = 'blah blarh bleh bleh blarh';\n\n// Use array_keys and array_values to extract search and replace arrays from the associative array\n$search = array_keys($searchreplace_array);\n$replace = array_values($searchreplace_array);\n\n// Perform multiple search and replace operations\n$result = str_replace($search, $replace, $string);\n\necho $result; // Outputs: bleh blerh bleh bleh blerh