<?php\n\nfunction decryptFormsAuthTicket($ticket, $decryptionKey)\n{\n    // Base64 decode the ticket cookie value\n    $binaryData = base64_decode($ticket);\n\n    // The first 16 bytes make up the IV (Initialization Vector)\n    $iv = substr($binaryData, 0, 16);\n    $encryptedData = substr($binaryData, 16);\n\n    // AES decryption example using mcrypt extension\n    $decrypted = openssl_decrypt(\n        $encryptedData,\n        'AES-256-CBC',\n        hex2bin($decryptionKey), // assuming hex format for key\n        OPENSSL_RAW_DATA|OPENSSL_ZERO_PADDING, // important flags\n        $iv\n    );\n\n    // Remove PKCS#7 padding\n    if ($decrypted !== false) {\n        $pad = ord(substr($decrypted,-1));\n        return substr($decrypted,0,-$pad);\n    }\n\n    return false;\n}\n\n// Example usage\n$cookieName = '.ASPXAUTH'; // Default name for the authentication cookie\n$ticket = $_COOKIE[$cookieName];\n$decryptionKey = 'your_decryption_key_here_in_hex';\n\n$decryptedTicket = decryptFormsAuthTicket($ticket, $decryptionKey);\nif ($decryptedTicket) {\n    // Process decrypted ticket here, parse the user data etc.\n    echo "Decrypted Ticket: " . htmlspecialchars($decryptedTicket);\n} else {\n    echo "Failed to decrypt.";\n}