<?php\n// Assuming $content holds the HTML content of the page you are parsing\n$content = file_get_contents('your_page_here.html');\n\n// Use a regular expression to find Megaupload links\npreg_match_all('/https?:\/\/(www\.)?megaupload\.com\/[\w\-\/.?=&]+/', $content, $matches);\n\n// Store found Megaupload links in the $links array\n$links = $matches[0];\n\n// Function to check each link\nfunction checkLinks($links) {\n    foreach ($links as $link) {\n        // Your existing link checking logic here\n        echo "Checking: " . htmlspecialchars($link) . "\n";\n        \n        // Example: Check if the URL is active\n        $headers = @get_headers($link);\n        if (strpos($headers[0], '200')) {\n            echo "Active\n";\n        } else {\n            echo "Inactive or invalid\n";\n        }\n    }\n}\n\n// Call the function with your Megaupload links\ncheckLinks($links);\n?>