<?php\n// Start of script or the section you want to measure\n$initialMemoryUsage = memory_get_usage();\n\n// Code goes here\n\n// End of script or the section you want to measure\n$finalMemoryUsage = memory_get_usage();\n$totalMemoryUsed = $finalMemoryUsage - $initialMemoryUsage;\n\n// Convert bytes to kilobytes and megabytes\n$totalMemoryUsedKB = $totalMemoryUsed / 1024;\n$totalMemoryUsedMB = $totalMemoryUsed / (1024 * 1024);\n\necho "Total memory used: $totalMemoryUsed bytes\n";\necho "Total memory used: $totalMemoryUsedKB KB\n";\necho "Total memory used: $totalMemoryUsedMB MB\n";\n?>