$albums = [];\n$result = $pdo->query("SELECT albums.aid, albums.name AS album_name, songs.sid, songs.length AS song_length FROM albums JOIN songs ON albums.aid = songs.aid");\n\nwhile ($row = $result->fetch(PDO::FETCH_ASSOC)) {\n    if (!isset($albums[$row['aid']])) {\n        $albums[$row['aid']] = ['name' => $row['album_name'], 'songs' => []];\n    }\n    $albums[$row['aid']]['songs'][] = ['sid' => $row['sid'], 'length' => $row['song_length']];\n}\n\n// Now, $albums contains a structured representation of albums and their songs.