// Assuming $conn is your MySQLi connection object\n$userId = $_SESSION["**"];\n$stmt = $conn->prepare("SELECT *, DATE_FORMAT(timestamp, '%W %D %M %Y') as formatted_date FROM articleDB WHERE userID=? ORDER BY timestamp DESC LIMIT 8");\n\n// Bind the session variable to the prepared statement\n$stmt->bind_param("i", $userId);\n\n// Execute and store the result\n$stmt->execute();\n$result = $stmt->get_result();\n\nwhile ($row = $result->fetch_assoc()) {\n    echo 'Posted: ' . htmlspecialchars($row['formatted_date']) . '<br>';\n}\n\n// Close the statement\n$stmt->close();