$domainURL = 'http://www.domain.com'; // Example domain\n$appendedTags = '?var1=2&var2=4';     // Variables to append\n\n$sHTML = preg_replace_callback(\n    "'href=\"($domainURL[^\"#]*?)\"'",\n    function($matches) use ($appendedTags) {\n        $url = $matches[1];\n        if (strpos($url, '?') === false) {\n            return 'href="' . $url . $appendedTags . '"';\n        } else {\n            return 'href="' . $url . '&' . substr($appendedTags, 1) . '"';\n        }\n    },\n    $sHTML\n);