<?php\n// Create connection\n$conn = new mysqli("localhost", "the_database_name", "the_database_password", "the_database_name");\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// SQL to create table\n$sql = "CREATE TABLE downloads_list (\ndownload_id INT(7) UNSIGNED AUTO_INCREMENT PRIMARY KEY,\ndownload_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\nvisitor_first_name VARCHAR(20) NOT NULL,\nvisitor_last_name VARCHAR(20) NOT NULL,\nvisitor_email VARCHAR(50) NOT NULL,\nvisitor_phone VARCHAR(30) NOT NULL,\nstore_name VARCHAR(20) NOT NULL,\ndocument_name VARCHAR(200) NOT NULL\n)";\n\nif ($conn->query($sql) === TRUE) {\n    echo "Table downloads_list created successfully";\n} else {\n    echo "Error creating table: " . $conn->error;\n}\n\n$conn->close();\n?>