<?php\n// Get requested URI\n$requestUri = $_SERVER['REQUEST_URI'];\n\n// Construct full path to the file\n$filePath = '/www/site' . $requestUri;\n\n// Check if file exists to prevent directory traversal attacks\nif (!file_exists($filePath)) {\n    header('HTTP/1.0 404 Not Found');\n    echo 'File not found.';\n    exit;\n}\n\n// Create a new finfo resource with MIME type flag\n$finfo = new finfo(FILEINFO_MIME_TYPE);\n\n// Fetch the MIME type of the file\n$mimeType = $finfo->file($filePath);\n\n// Set the Content-Type header to the detected MIME type\nheader('Content-Type: ' . $mimeType);\n\n// Read and output the file content\nreadfile($filePath);\n?>