<?php\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    // Update settings based on checkbox input\n    foreach ($settings as $id => $setting) {\n        $isChecked = in_array($id, $_POST['checkbox'] ?? []) ? 1 : 0;\n        \n        // Connect to the database and update the setting\n        $conn = new mysqli($servername, $username, $password, $dbname);\n        if ($conn->connect_error) {\n            die("Connection failed: " . $conn->connect_error);\n        }\n\n        $stmt = $conn->prepare("UPDATE settings SET is_checked = ? WHERE setting_id = ?");\n        $stmt->bind_param("ii", $isChecked, $id);\n        $stmt->execute();\n\n        $stmt->close();\n        $conn->close();\n    }\n}\n?>