// Assuming $form is your form object and you've added the 'terms' checkbox to it\n$terms = new Zend_Form_Element_Checkbox('terms');\n$terms->setLabel('I agree with terms and conditions')\n      ->setRequired(true)\n      // Add a custom validator for checking if the box is checked\n      ->addValidator('NotEmpty', true, array(\n          'messages' => array(\n              Zend_Validate_NotEmpty::IS_EMPTY => "You must agree with terms and conditions."\n          )\n      ));\n\n$form->addElement($terms);