<?php\n$last1Min = time() - 60;\n$last5Mins = time() - 300;\n$last1Hr = time() - 3600; // Corrected from 6000 to 3600 for 1 hour\n$last1Dy = time() - 86400; // Corrected from 144000 to 86400 for 1 day\n$last1Wk = time() - 604800; // Corrected from 1008000 to 604800 for 1 week\n$last1Mnth = time() - 2592000; // Corrected from 30240000 to 2592000 for 1 month\n\n$sql = "\n    SELECT \n        SUM(CASE WHEN lastOnline > $last1Min THEN 1 ELSE 0 END) AS last1Min,\n        SUM(CASE WHEN lastOnline > $last5Mins THEN 1 ELSE 0 END) AS last5Mins,\n        SUM(CASE WHEN lastOnline > $last1Hr THEN 1 ELSE 0 END) AS last1Hr,\n        SUM(CASE WHEN lastOnline > $last1Dy THEN 1 ELSE 0 END) AS last1Dy,\n        SUM(CASE WHEN lastOnline > $last1Wk THEN 1 ELSE 0 END) AS last1Wk,\n        SUM(CASE WHEN lastOnline > $last1Mnth THEN 1 ELSE 0 END) AS last1Mnth\n    FROM usersonline";\n\n$result = mysql_query($sql);\nif ($result && mysql_num_rows($result) > 0) {\n    $row = mysql_fetch_assoc($result);\n    echo "Users online in the last minute: " . $row['last1Min'] . "<br />";\n    echo "Users online in the last 5 minutes: " . $row['last5Mins'] . "<br />";\n    echo "Users online in the last hour: " . $row['last1Hr'] . "<br />";\n    echo "Users online in the last day: " . $row['last1Dy'] . "<br />";\n    echo "Users online in the last week: " . $row['last1Wk'] . "<br />";\n    echo "Users online in the last month: " . $row['last1Mnth'] . "<br /><br />";\n}\n?>