<?php\nfunction getMimeType($filename) {\n    // Initialize return value\n    $mime = '';\n\n    // Check if the 'file' command exists and is executable\n    if (is_executable('/usr/bin/file')) {\n        exec('/usr/bin/file --mime-type -b ' . escapeshellarg($filename), $output, $return_var);\n        \n        // Check for successful execution and valid output\n        if ($return_var === 0 && isset($output[0])) {\n            $mime = trim($output[0]);\n        }\n    }\n\n    return $mime;\n}\n\n// Example usage\n$filename = '/path/to/your/file.ext';\necho getMimeType($filename);\n?>