<?php\nheader('Content-Type: text/xml');\n\n$conn = new mysqli("localhost", "yourUsername", "yourPassword", "yourDatabase");\n\nif ($conn->connect_error) {\n    die("<error>Connection failed: " . $conn->connect_error . "</error>");\n}\n\n$xmlData = $_POST['xml'];\n$xml = simplexml_load_string($xmlData);\n\n$user = $conn->real_escape_string((string)$xml->user);\n$pass = $conn->real_escape_string((string)$xml->pass);\n\n$sql = "INSERT INTO users (username, password) VALUES ('$user', '$pass')";\n\nif ($conn->query($sql) === TRUE) {\n    echo "<response>Success</response>";\n} else {\n    echo "<error>Error: " . $sql . "<br>" . $conn->error . "</error>";\n}\n\n$conn->close();\n?>