function isJpegCorrupted($filename) {\n    // Check if the file extension and MIME type are valid for JPEG\n    $validMimeTypes = ['image/jpeg', 'image/pjpeg'];\n    \n    // Get MIME type using exif_imagetype which checks file headers\n    $fileType = exif_imagetype($filename);\n    if (!in_array($fileType, [IMAGETYPE_JPEG])) {\n        return true; // Not a JPEG or corrupted header\n    }\n    \n    // Read the first few bytes to verify JPEG signature\n    $handle = fopen($filename, "rb");\n    $signature = fread($handle, 2);\n    fclose($handle);\n\n    if ($signature !== "\xFF\xD8") {\n        return true; // Invalid JPEG start signature\n    }\n\n    return false; // File seems valid\n}\n\n// Usage\n$filename = 'path/to/your/image.jpg';\nif (isJpegCorrupted($filename)) {\n    echo "The file is corrupted or invalid.";\n} else {\n    echo "The file appears to be a valid JPEG.";\n}