$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "myDB";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$values = [];\n$data = [\n    ['value1a', 'value2a'],\n    ['value1b', 'value2b'],\n    // Add more data rows as needed\n];\n\nforeach ($data as $row) {\n    $values[] = "('" . implode("','", $conn->real_escape_string_array($row)) . "')";\n}\n\n$sql = "INSERT INTO MyTable (column1, column2) VALUES " . implode(',', $values);\n\nif ($conn->query($sql) === TRUE) {\n    echo "New records created successfully";\n} else {\n    echo "Error: " . $sql . "<br>" . $conn->error;\n}\n\n$conn->close();\n\n// Helper function to escape an array of strings\nfunction real_escape_string_array(mysqli $link, array $array) {\n    return array_map([$link, 'real_escape_string'], $array);\n}