// Friends' user IDs as CSV\n$friendsCSV = "3,4,5,6,7";\n\n// Convert CSV to array\n$friendsArray = explode(",", $friendsCSV);\n\ntry {\n    // Connect to database (replace with actual connection details)\n    $pdo = new PDO('mysql:host=localhost;dbname=your_database', 'username', 'password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Prepare the SQL statement to get distinct transactions involving any of the friends\n    $query = "SELECT DISTINCT * FROM transaction WHERE user1 IN (:friends) OR user2 IN (:friends)";\n    \n    // Create a string of placeholders for the IN clause\n    $placeholders = implode(',', array_fill(0, count($friendsArray), ':friend'));\n\n    // Update query to use placeholders\n    $query = str_replace(':friends', $placeholders, $query);\n    \n    $stmt = $pdo->prepare($query);\n\n    // Bind each friend ID as a parameter\n    foreach ($friendsArray as $i => $id) {\n        $stmt->bindParam(":friend$i", $friendsArray[$i], PDO::PARAM_INT);\n    }\n\n    // Execute the query\n    $stmt->execute();\n\n    // Fetch results\n    $transactions = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n    // Output transactions (for testing purposes)\n    foreach ($transactions as $transaction) {\n        echo "Transaction ID: {$transaction['tid']} - User1: {$transaction['user1']} - User2: {$transaction['user2']} - Type of Trade: {$transaction['type_of_trade']}\n";\n    }\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}