class Application_Form_RecordForm extends Zend_Form {\n         protected function _determineAction() {\n             $action = $this->getValue('action');\n             switch ($action) {\n                 case 'edit':\n                     return 'edit';\n                 case 'delete':\n                     return 'delete';\n                 default:\n                     return null;\n             }\n         }\n\n         public function processSubmit($postData) {\n             if (!$this->isValid($postData)) return false;\n\n             $data = $this->getValues();\n             switch ($this->_determineAction()) {\n                 case 'edit':\n                     // Edit logic\n                     break;\n                 case 'delete':\n                     // Delete logic\n                     break;\n             }\n         }\n     }