<?php\n$args = array(\n    'category' => 1,\n    'orderby' => 'title',\n    'order' => 'ASC'\n);\n\n$all_posts = get_posts($args);\n$total_posts = count($all_posts);\n\nfor ($i = 0; $i < $total_posts; $i += 3) {\n    echo '<li>';\n    for ($j = $i; $j < min($i + 3, $total_posts); $j++) {\n        setup_postdata($all_posts[$j]);\n        echo '<a href="' . get_permalink($all_posts[$j]->ID) . '">' . get_the_title($all_posts[$j]->ID) . '</a>';\n        if ($j < min($i + 2, $total_posts - 1)) {\n            echo ' | ';\n        }\n    }\n    echo '</li>';\n}\nwp_reset_postdata();\n?>