use Doctrine\ORM\EntityManagerInterface;\n\n   public function debugEntity($entity, EntityManagerInterface $entityManager)\n   {\n       $meta = $entityManager->getClassMetadata(get_class($entity));\n       echo "Entity of type: " . get_class($entity) . PHP_EOL;\n       foreach ($meta->getFieldNames() as $fieldName) {\n           echo "->" . $fieldName . ": " . $entity->{'get' . ucfirst($fieldName)}() . PHP_EOL;\n       }\n   }