<?php\n// Get the URI path parts\n$request_uri = $_SERVER['REQUEST_URI'];\n$path_parts = explode('/', trim($request_uri, '/'));\n\n// Check if the format matches /number/text\nif (count($path_parts) >= 2 && is_numeric($path_parts[0])) {\n    $story_id = $path_parts[0];\n    $story_slug = $path_parts[1]; // Optional: you can use this for SEO but not mandatory\n\n    // Now, use the story_id to fetch data from your database\n    echo "Displaying story with ID: $story_id";\n} else {\n    http_response_code(404);\n    echo "Page not found.";\n}