<?php\n\n/**\n * Generates a cryptographically secure random string.\n *\n * @param int $length The length of the string to generate.\n * @return string A randomly generated secure string of the specified length.\n */\nfunction generateSecureRandomString($length = 6) {\n    // Define the characters you want in your random string.\n    $characters = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';\n    \n    // Get the total number of available characters.\n    $charactersLength = strlen($characters);\n    \n    // Initialize an empty string to store the result.\n    $randomString = '';\n    \n    // Loop through and append a cryptographically secure random character from $characters for each position in the string.\n    for ($i = 0; $i < $length; $i++) {\n        // Randomly select an index within the range of $charactersLength securely.\n        $randomIndex = random_int(0, $charactersLength - 1);\n        \n        // Append the character at $randomIndex to $randomString.\n        $randomString .= $characters[$randomIndex];\n    }\n    \n    return $randomString;\n}\n\n// Example usage:\necho generateSecureRandomString(6); // Generates a cryptographically secure random string of length 6