class Application_Controller_RecordController extends Zend_Controller_Action {\n         public function addAction() {\n             $form = new Application_Form_UserForm();\n             if ($this->getRequest()->isPost()) {\n                 if ($form->processSubmit($this->_request->getPost())) {\n                     $this->_helper->redirector('index');\n                 }\n             }\n             $this->view->form = $form;\n         }\n     }