function do_http_request($url, $xml_data, $method = 'POST', $optional_headers = null) {\n    // Validate the XML data\n    libxml_use_internal_errors(true);\n    simplexml_load_string($xml_data);\n    if(libxml_get_errors()) {\n        throw new Exception('XML data is not well-formed.');\n    }\n    libxml_clear_errors();\n\n    $params = array(\n        'http' => array(\n            'method' => $method,\n            'content' => $xml_data\n        )\n    );\n\n    if ($optional_headers !== null) {\n        $params['http']['header'] = $optional_headers;\n    } else {\n        $params['http']['header'] = 'Content-Type: application/xml';\n    }\n\n    $ctx = stream_context_create($params);\n    $fp = fopen($url, 'rb', false, $ctx);\n    if (!$fp) {\n        throw new Exception("Problem with $url");\n    }\n\n    $response = @stream_get_contents($fp);\n    if ($response === false) {\n        throw new Exception("Problem reading data from $url");\n    }\n\n    fclose($fp);\n\n    return $response;\n}\n\n$xml_data = '<Root><Item>Hello</Item></Root>'; // Example XML, replace with your actual XML\n$response = do_http_request('http://apitestserver.co.uk:9000/Service.svc/Items', $xml_data);\necho $response;