<?php\n$rtfFilePath = 'path/to/your/document.rtf';\n$outputImageFilePath = 'path/to/output/image.jpg';\n\n// Command to convert RTF to JPG using LibreOffice in headless mode\n$command = escapeshellcmd("soffice --headless --convert-to jpg:writer_WEB_pdf --outdir " . dirname($outputImageFilePath) . " " . $rtfFilePath);\n\n// Execute the command\nshell_exec($command);\n\necho "Conversion done. Check your output directory for the converted file.";\n?>