<?php\nfunction getBusinessWeeks($startDate, $endDate) {\n    $start = new DateTime($startDate);\n    $end = new DateTime($endDate);\n    $interval = new DateInterval('P1W');\n    \n    // Ensure the start date is a Monday\n    if ($start->format('N') != 1) { // If not Monday\n        $start->modify('next monday');\n    }\n    \n    while ($start <= $end) {\n        $weekStart = clone $start;\n        $weekEnd = clone $start;\n        $weekEnd->modify('+4 days'); // Friday\n\n        if ($weekEnd > $end) { // If end of week is after end date, adjust it\n            $weekEnd->modify('-' . ($weekEnd->diff($end)->days + 1) . ' days');\n        }\n        \n        echo $weekStart->format('M d') . ' - ' . $weekEnd->format('M d') . PHP_EOL;\n        $start->add($interval);\n    }\n}\n\n// Example usage\ngetBusinessWeeks('2009-03-02', '2010-12-31');\n?>