<?php\nrequire_once 'sphinxapi.php';\n\n$cl = new SphinxClient();\n$cl->SetServer('localhost', 9312);\n\n// Assuming you have an index named 'my_index' and you want to group by 'group_id'\n// and sum the attribute 'score'\n\n$query = '';\n$index = 'my_index';\n$filter = [];\n$sortby = '@sum score'; // Sort by the sum of the 'score' attribute\n\n$cl->SetSortMode(SPH_SORT_EXTENDED, $sortby);\n$cl->GroupBy('group_id', SPH_GROUPBY_ATTR); // Grouping by 'group_id'\n\n$result = $cl->Query($query, $index);\n\nif ($result === false)\n{\n    echo "Query failed: " . $cl->GetLastError() . ".\n";\n}\nelseif (empty($result['matches']))\n{\n    echo "Matches not found.\n";\n}\nelse\n{\n    print_r($result);\n}\n\n?>