// Assume these values are sanitized and validated\n$startHours = $_GET['start_hours'];\n$startMinutes = $_GET['start_minutes'];\n$startAMPM = $_GET['start_ampm'];\n\n// Create a DateTime object from the given time string\n$dateTime = DateTime::createFromFormat('h:i A', "{$startHours}:{$startMinutes} {$startAMPM}");\n\nif ($dateTime) {\n    // Modify the date by adding one day\n    $dateTime->modify('+1 day');\n    \n    // Format the new date and time as a string\n    $newDateTimeString = $dateTime->format('Y-m-d H:i:s');\n    echo $newDateTimeString;\n} else {\n    echo "Invalid date format.";\n}