<?php\nsession_start();\n\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    if (!empty($_FILES)) {\n        $file = $_FILES['fileToUpload'];\n        $uploadPath = __DIR__ . '/uploads/' . basename($file['name']);\n        \n        // Start tracking the upload progress\n        $_SESSION['upload_progress'][$_POST["PHP_SESSION_UPLOAD_PROGRESS"]] = ['uploaded' => 0, 'size' => $file['size']];\n        \n        move_uploaded_file($file['tmp_name'], $uploadPath);\n        \n        // End tracking the upload progress\n        unset($_SESSION['upload_progress'][$_POST["PHP_SESSION_UPLOAD_PROGRESS"]]);\n\n        echo "File uploaded successfully!";\n    }\n}\n?>