function sanitize_user_input($input) {\n    // Define tags that are allowed\n    $allowed_tags = '<b><br><p><a>';\n\n    // Strip unwanted tags but allow the ones specified\n    $output = strip_tags($input, $allowed_tags);\n\n    return $output;\n}\n\n// Example usage\n$user_comment = "<div>This is a <b>bold</b> comment with a <script>alert('XSS');</script> tag and a break<br />line.</div>";\n$safe_comment = sanitize_user_input($user_comment);\necho $safe_comment;  // Output: This is a <b>bold</b> comment with a <br />line.