public function ajaxAction() {\n    // Check if the request is an AJAX request\n    if ($this->request->is('ajax')) {\n        $this->autoRender = false; // Disable auto rendering\n        $this->layout = false; // No layout\n\n        // Assuming you have some data to pass\n        $data = ['key' => 'value'];\n        \n        // Render the view file with AJAX data\n        echo $this->render('/ajax_partial', $data);\n    }\n}