<?php\n\nfunction show($pdo)\n{\n    $stmt = $pdo->query('SELECT DATABASE()');\n    echo $stmt->fetchColumn() . "<br>";\n}\n\n// New connection with PDO\n$dsn1 = 'mysql:host=localhost;dbname=dredd';\n$options = [\n    PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,\n];\n$pdo1 = new PDO($dsn1, 'root', '', $options);\n\nshow($pdo1); // Use PDO for new code\n\n// Legacy connection with PDO\n$dsn2 = 'mysql:host=localhost;dbname=afup';\n$pdo2 = new PDO($dsn2, 'root', '', $options);\nshow($pdo2); // This uses the legacy connection explicitly\n\n?>