<?php\nsession_start();\n\n// Generate a token for the session\nif (!isset($_SESSION['token'])) {\n    $_SESSION['token'] = bin2hex(random_bytes(32));\n}\n\nfunction get_audio_stream($file_name, $expected_token) {\n    if (isset($_GET['token']) && ($_GET['token'] === $expected_token)) {\n        // Your path to the mp3 files\n        $mp3_path = '/path/to/your/mp3/files/' . basename($file_name);\n\n        if (file_exists($mp3_path)) {\n            header('Content-Type: audio/mpeg');\n            readfile($mp3_path);\n        } else {\n            http_response_code(404);\n        }\n    } else {\n        http_response_code(403);\n    }\n}\n\n// Usage example\n// Assume the player sends a request with ?token=xxx&file=music.mp3\nif (isset($_REQUEST['file'])) {\n    get_audio_stream($_REQUEST['file'], $_SESSION['token']);\n}\n?>