<?php\n\nfunction stream_tweets() {\n    $url = "https://stream.twitter.com/1.1/statuses/filter.json";\n    $params = "follow=USER_ID"; // replace USER_ID with user ID you want to follow\n    $oauth_access_token = "YOUR_ACCESS_TOKEN";\n    $oauth_access_token_secret = "YOUR_ACCESS_TOKEN_SECRET";\n    $consumer_key = "YOUR_CONSUMER_KEY";\n    $consumer_secret = "YOUR_CONSUMER_SECRET";\n\n    $process = curl_init($url . "?" . $params);\n\n    // Set up OAuth header\n    $oauth_nonce = time();\n    $signature_key = rawurlencode($consumer_secret) . "&" . rawurlencode($oauth_access_token_secret);\n    $base_string_params = http_build_query([\n        'oauth_consumer_key' => $consumer_key,\n        'oauth_nonce' => $oauth_nonce,\n        'oauth_signature_method' => 'HMAC-SHA1',\n        'oauth_timestamp' => time(),\n        'oauth_token' => $oauth_access_token,\n        'oauth_version' => '1.0'\n    ]);\n    $base_string = "POST&" . rawurlencode($url) . "&" . rawurlencode($base_string_params);\n    $signature = base64_encode(hash_hmac('sha1', $base_string, $signature_key, true));\n    $headers = [\n        "Authorization: OAuth oauth_consumer_key=\"$consumer_key\",oauth_nonce=\"$oauth_nonce\"," .\n                         "oauth_signature=\"$signature\"," .\n                         "oauth_signature_method=\"HMAC-SHA1\"," .\n                         "oauth_timestamp=\"" . time() . "\"," .\n                         "oauth_token=\"$oauth_access_token\"," .\n                         "oauth_version=\"1.0\""\n    ];\n\n    // cURL options\n    curl_setopt($process, CURLOPT_HTTPHEADER, $headers);\n    curl_setopt($process, CURLOPT_HEADER, false);\n    curl_setopt($process, CURLOPT_USERAGENT, $_SERVER['HTTP_USER_AGENT']);\n    curl_setopt($process, CURLOPT_RETURNTRANSFER, true);\n    curl_setopt($process, CURLOPT_TIMEOUT, 0); // Keep the connection open\n\n    // Execute cURL and read stream\n    $buffer = fopen('php://stdout', 'w'); // Write to stdout or can be a file, db etc.\n    curl_setopt($process, CURLOPT_FILE, $buffer);\n\n    while (true) {\n        curl_exec($process);\n        if (curl_errno($process)) {\n            echo 'Error:' . curl_error($process);\n            break;\n        }\n        sleep(10); // Wait for 10 seconds before reconnecting in case of any error\n    }\n\n    fclose($buffer);\n    curl_close($process);\n}\n\nstream_tweets();