<?php\nsession_start();\n\n// Function to add item to cart\nfunction addToCart($itemId) {\n    if (!isset($_SESSION['cart'])) {\n        $_SESSION['cart'] = array();\n    }\n    // Check if item already exists in the cart\n    if (array_key_exists($itemId, $_SESSION['cart'])) {\n        $_SESSION['cart'][$itemId]++;\n    } else {\n        $_SESSION['cart'][$itemId] = 1;\n    }\n}\n\n// Function to get cart items in XML format\nfunction getCartXML() {\n    $xmlString = "<cart>";\n    if (isset($_SESSION['cart'])) {\n        foreach ($_SESSION['cart'] as $id => $quantity) {\n            $xmlString .= "<item id='$id' quantity='$quantity'/>";\n        }\n    }\n    $xmlString .= "</cart>";\n    return simplexml_load_string($xmlString);\n}\n\n// Example usage: Add an item and get cart XML\nif (isset($_GET['addItem'])) {\n    addToCart($_GET['addItem']);\n}\n\n$cartXml = getCartXML();\n\n$xslFilePath = 'path/to/cart.xslt'; // Path to your XSLT file\n\n$xslDoc = new DOMDocument();\n$xslDoc->load($xslFilePath);\n\n// Configure the transformer\n$proc = new XSLTProcessor();\n$proc->importStylesheet($xslDoc);\necho $proc->transformToXML($cartXml);\n?>