<?php\n// List of allowed table names\n$allowedTables = ['users', 'products'];\n\n// User-provided table name (e.g., from request)\n$userTable = $_GET['table']; // Example, should be sanitized and validated\n\n// Check if the provided table name is allowed\nif (!in_array($userTable, $allowedTables)) {\n    throw new Exception("Invalid table name.");\n}\n\n// Prepare the SQL statement with the approved table name\n$sql = 'SELECT * FROM ' . $userTable . ' WHERE 1';\n$stmt = $dbh->prepare($sql);\n\nif ($stmt->execute()) {\n    var_dump($stmt->fetchAll());\n}\n?>