<?php\n// Sample data array\n$data = [\n    ['Name' => 'Apple', 'Price' => 2.99],\n    ['Name' => 'Banana', 'Price' => 1.99],\n    ['Name' => 'Cherry', 'Price' => 3.49],\n];\n\n// Get sort parameters from URL\n$sort = isset($_GET['sort']) ? $_GET['sort'] : 'Name';\n$order = isset($_GET['order']) && $_GET['order'] === 'asc' ? 'asc' : 'desc';\n\n// Sort the data array\nusort($data, function ($a, $b) use ($sort, $order) {\n    if ($a[$sort] == $b[$sort]) {\n        return 0;\n    }\n    return ($a[$sort] < $b[$sort]) ? -1 : 1;\n});\n\n// Toggle order\n$order = $order === 'asc' ? 'desc' : 'asc';\n?>\n\n<!DOCTYPE html>\n<html>\n<head>\n    <title>Sortable Table</title>\n</head>\n<body>\n<table border="1">\n    <tr>\n        <th><a href="?sort=Name&order=<?php echo htmlspecialchars($order); ?>">Name</a></th>\n        <th><a href="?sort=Price&order=<?php echo htmlspecialchars($order); ?>">Price</a></th>\n    </tr>\n    <?php foreach ($data as $row): ?>\n        <tr>\n            <td><?php echo htmlspecialchars($row['Name']); ?></td>\n            <td><?php echo htmlspecialchars(number_format($row['Price'], 2)); ?></td>\n        </tr>\n    <?php endforeach; ?>\n</table>\n</body>\n</html>