foreach ($rows as $row) {\n    if (is_null($row['firstname']) || $row['firstname'] == "") {\n        $row['firstname'] = "No Info"; // Use '=' for assignment\n    }\n    if (is_null($row['lastname']) || $row['lastname'] == "") {\n        $row['lastname'] = "No Info"; // Correctly set lastname and use '=' for assignment\n    }\n\n    echo '<tr>' . "\n";\n    echo '<td><a href="#" onclick="updateByUser(\''. htmlspecialchars($row['username'], ENT_QUOTES) .'\',\''.$subcat.'\')">'.$row['username'].'</a></td>' . "\n";\n    echo '<td><a href="#" onclick="updateByUser(\''. htmlspecialchars($row['username'], ENT_QUOTES) .'\', \''.$subcat.'\')">'.$row['firstname'].'</a></td>' . "\n";\n    echo '<td><a href="#" onclick="updateByUser(\''. htmlspecialchars($row['username'], ENT_QUOTES) .'\', \''.$subcat.'\')">'.$row['lastname'].'</a></td>' . "\n";\n    echo '</tr>' . "\n";\n}