function createSlug($title) {\n    // Remove special characters (except spaces)\n    $slug = preg_replace('/[^A-Za-z0-9-]+/', '-', strtolower($title));\n    // Trim hyphens from the beginning or the end\n    return trim($slug, '-');\n}\n\nfunction generatePostURL($postId, $postTitle) {\n    $baseURL = "http://www.example.com/blog/";\n    $slug = createSlug($postTitle);\n    return $baseURL . $postId . "-" . $slug;\n}\n\n// Example usage\n$postId = 987;\n$postTitle = 'Virtual Camaraderie A Persistent Video Portal For The Remote Worker';\necho generatePostURL($postId, $postTitle); // Outputs: http://www.example.com/blog/987-virtual-camaraderie-a-persistent-video-portal-for-the-remote-worker