$pdo = new PDO('mysql:host=localhost;dbname=testdb', 'user', 'pass');\n\n// Assuming you have a table with a LONGBLOB column named `data`\n$sql = "INSERT INTO your_table (data) VALUES (:data)";\n$stmt = $pdo->prepare($sql);\n\n// Binary data can be stored as a string in PHP\n$binaryData = file_get_contents('path/to/your/largefile.bin');\n\n$stmt->bindParam(':data', $binaryData, PDO::PARAM_LOB);\n$stmt->execute();