// Assuming these are your models\nclass UserModel extends Zend_Db_Table_Abstract {\n    protected $_name = 'users';\n}\n\nclass OrderModel extends Zend_Db_Table_Abstract {\n    protected $_name = 'orders';\n}\n\n// Service class to handle complex queries and logic involving multiple tables\nclass UserService {\n    private $userModel;\n    private $orderModel;\n\n    public function __construct() {\n        $this->userModel = new UserModel();\n        $this->orderModel = new OrderModel();\n    }\n\n    // Example method to get user details along with their orders\n    public function getUserOrders($userId) {\n        $select = $this->userModel->select()\n            ->setIntegrityCheck(false)\n            ->from(array('u' => 'users'), array('*'))\n            ->join(array('o' => 'orders'), 'u.id = o.user_id', array('*'))\n            ->where('u.id = ?', (int)$userId);\n\n        return $this->userModel->fetchAll($select)->toArray();\n    }\n}