// Read the raw POST data\n$json = file_get_contents('php://input');\n\n// Decode the JSON into an associative array\n$data = json_decode($json, true);\n\n// Check if 'screencast' is set in the decoded data\nif (isset($data['screencast'])) {\n    // Access the subject or other fields here\n    echo $data['screencast']['subject'];\n} else {\n    echo "No screencast data found.";\n}