$query = "SELECT DISTINCT maoie FROM ".$BD."site";\n$res = mysql_query($query);\n$output .= '<td valign="top"><form name="form5" method="GET" action=""><select name="m" onchange="this.form.submit()">';\nif ( empty($_GET['p']) ) $output .= '<option value=" ">All</option>';\nelse $output .= '<option value="'.htmlspecialchars($_GET['m'], ENT_QUOTES, 'UTF-8').'">'.htmlspecialchars($_GET['m'], ENT_QUOTES, 'UTF-8').'</option>';\n\nwhile ( $row = mysql_fetch_assoc($res) ) {\n    if( !empty($row['maoie']) ) {\n        $output .= '<option value="'.htmlspecialchars($row['maoie'], ENT_QUOTES, 'UTF-8').'">'.htmlspecialchars($row['maoie'], ENT_QUOTES, 'UTF-8').'</option>'; \n    }\n}\n$output .= '</select></form></td>';\necho $output;