try {\n    // Create a new PDO instance\n    $pdo = new PDO("sqlsrv:server=your_server;Database=your_database", "username", "password");\n    // Set error mode to exception\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Prepare an SQL statement for execution\n    $stmt = $pdo->prepare("INSERT INTO your_table (column1) VALUES (:value)");\n\n    // Bind a value to the corresponding named placeholder\n    $valueToInsert = "your_value_here";\n    $stmt->bindParam(':value', $valueToInsert);\n\n    // Execute the prepared statement\n    $stmt->execute();\n\n} catch (PDOException $e) {\n    // Handle error\n    die("Could not connect to the database $dbname :" . $e->getMessage());\n}