function get_newest_tabs() \n{\n    $db = new mysqli('host', 'user', 'password', 'database'); // Update with your SQL credentials\n\n    if ($db->connect_error) {\n        die("Connection failed: " . $db->connect_error);\n    }\n\n    $sql = "\n        SELECT tabs.tab_id, tabs.song_id, tabs.user_id, tabs.tab_version, tabs.number_of_hits, artist.artist_name, users.user_alias, songs.song_name, tabs.time_added\n        FROM tabs\n        INNER JOIN users ON tabs.user_id = users.user_id \n        INNER JOIN songs ON tabs.song_id = songs.song_id \n        INNER JOIN artist ON songs.artist_id = artist.artist_id\n        ORDER BY tabs.time_added DESC \n        LIMIT 15;\n    ";\n\n    $result = $db->query($sql);\n\n    if ($result) {\n        $tabsArray = [];\n        while ($row = $result->fetch_assoc()) {\n            $tabsArray[] = [\n                'tab_id' => $row['tab_id'],\n                'song_name' => $row['song_name'],\n                'artist_name' => $row['artist_name'],\n                'user_alias' => $row['user_alias'],\n                'tab_version' => $row['tab_version'],\n                'number_of_hits' => $row['number_of_hits'],\n                'time_added' => $row['time_added']\n            ];\n        }\n        return $tabsArray;\n    } else {\n        die("Query failed: " . $db->error);\n    }\n\n    $db->close();\n}