function markdownToHtml($text) {\n    // Function to replace markdown italics with HTML emphasis tags\n    $text = preg_replace_callback('/\*(.*?)\*/', function($matches, $count = 0) {\n        static $toggle = false;\n        $toggle = !$toggle;\n        return $toggle ? '<em>' . htmlspecialchars($matches[1]) . '</em>' : htmlspecialchars($matches[1]);\n    }, $text);\n\n    // Function to replace markdown code blocks with HTML code tags\n    $text = preg_replace_callback('/`(.*?)`/', function($matches, $count = 0) {\n        static $toggle = false;\n        $toggle = !$toggle;\n        return $toggle ? '<code>' . htmlspecialchars($matches[1]) . '</code>' : htmlspecialchars($matches[1]);\n    }, $text);\n\n    return $text;\n}\n\n// Example usage\n$markdownText = "*italic* and `code`, *another italic* and `more code`.";\n$htmlText = markdownToHtml($markdownText);\necho $htmlText;